import React from "react"
import FormInput from "../FormInput/FormInput.component";



class SignIn extends React.Component {
   constructor(props) {
      super(props);
      this.state = {
         password: "",
         email: "",

      }
   }
   handleSubmit = (event) => {
      event.preventDefault();
      this.setState({ email: '', password: '' });

   }
   handleChange = (event) => {
      const { value, name } = event.target;
      this.setState({ [name]: value });
   }

   render() {
      return (
         <div className="sign-in">
            <h2 className="sign__title">I already have an account</h2>
            <span className="sign__subtitle">Sign in with your email and password</span>

            <form onSubmit={this.handleSubmit}>
               <FormInput type="email" name="email" value={this.state.email} onChange={this.handleChange} required />

               <label htmlFor="email">Email</label>
               <input type="password" name="password" value={this.state.password} onChange={this.handleChange} required />
               <label htmlFor="password">Password</label>
               <input type="submit" value="Submit Form" />
            </form>
         </div>
      );
   }
}


export default SignIn;
